import * as crypto from 'crypto';
import oauthSignature from 'oauth-signature';

/**
 * A helper for OAuth 1.0 header information and signing keys.
 */
export class OAuthHelper {
  /**
   * Intialize the helper
   * @param {string} consumerKey The consumer key
   * @param {string} token The token value
   */
  constructor(consumerKey, token) {
    /** @type {string} */
    this.consumerKey = consumerKey;
    /** @type {string} */
    this.token = token;
    /** @type {string} */
    this.nonce = crypto.randomBytes(16).toString('hex');
    /** @type {string} */
    this.signature = '';
    /** @type {string} */
    this.timestamp = Math.round(Date.now() / 1000).toString();
  }

  get parameters() {
    return {
      oauth_consumer_key: this.consumerKey,
      oauth_token: this.token,
      oauth_nonce: this.nonce,
      oauth_timestamp: this.timestamp,
      oauth_signature_method: 'HMAC-SHA1',
      oauth_version: '1.0',
    };
  }

  get header() {
    let str = 'OAuth ';

    let params = Object.assign({ realm: '' }, this.parameters);
    params['oauth_signature'] = this.signature;

    let keys = Object.keys(params);
    let pairs = keys.map((k) => {
      return k + '="' + params[k] + '"';
    });

    str += pairs.join(',');
    return str;
  }

  get queryString() {
    let params = Object.assign({}, this.parameters);
    params['oauth_signature'] = this.signature;
    let str = JSON.stringify(params);
    return 'Authorization=' + str;
  }

  /**
   * Sign the request with oath headers and sets the signature member of the OauthHelper instance.
   */
  sign(url, request, consumerSecret, tokenSecret) {
    let params = Object.assign({}, this.parameters, request.params.toObject());

    this.signature = oauthSignature.generate(
      request.method,
      url,
      params,
      consumerSecret,
      tokenSecret,
    );
  }
}

/**
 * Creates a request to use with the client.
 */
export class BricklinkRequest {
  /**
   * Create a new request.
   * @param {string} method The type of HTTP request to perform.
   * @param {string} uri The relative of full path uri of a request to perform. This should not include any query parameters.
   * @param {RequestParams} [params] Additional parameters to include with the request in either post body or query params.
   * @param {BricklinkRequestCallback} [callback=null] A callback function to perform after the request has been successfully performed.
   */
  constructor(method, uri, params, callback) {
    /** @type {string} */
    this.method = method;
    /** @type {string} */
    this.uri = uri;
    /** @type {RequestParams} */
    this.params = params || new RequestParams();
    /** @type {BricklinkRequestCallback|null} */
    this.callback = callback || null;
  }
  /** @type {string} */
  static get GET() {
    return 'get';
  }

  /** @type {string} */
  static get PUT() {
    return 'put';
  }

  /** @type {string} */
  static get POST() {
    return 'post';
  }

  /** @type {string} */
  static get DELETE() {
    return 'put';
  }
}

/**
 * @callback BricklinkRequestCallback
 * @param {any} value
 * @returns {any}
 */

/**
 * Represents any query parameters to use with a request.
 */
export class RequestParams {
  /**
   * Convert params for the request to a query string.
   * @return {string} parameters in the format of '?<param>=<value>'.
   */
  toQueryString() {
    const pairs = [];
    for (const member in this) {
      if (this[member] != null) {
        pairs.push(member + '=' + this[member]);
      }
    }
    if (pairs.length > 0) {
      return '?' + pairs.join('&');
    }
    return '';
  }

  /**
   * Convert params for the request to a an object.
   * @return {object} trimmed down parameters object.
   */
  toObject() {
    /** @type {object} */
    const params = {};
    for (const member in this) {
      if (this[member] != null) {
        params[member] = this[member];
      }
    }
    return params;
  }
}

# bricklink-api

[![Node.js CI](https://github.com/ryansh100/bricklink-api/actions/workflows/node.js.yml/badge.svg)](https://github.com/ryansh100/bricklink-api/actions/workflows/node.js.yml)

Node package for connecting to Bricklink's API.

```
npm install --save bricklink-api
```

## Basic Usage

Initialize a client with your OAuth credentials as supplied at the following:

[https://www.bricklink.com/v2/api/register_consumer.page](https://www.bricklink.com/v2/api/register_consumer.page)

You are required to have a Bricklink account and register your IP address from
which your application will be using the API. You can also use 0.0.0.0 to match
any IP address (such as a cloud solution); however, you would need to keep the keys and secrets secure.

```javascript
var api = require('bricklink-api');
var Client = api.Client,
    ItemType = api.ItemType;

var bricklink = new Client({
    "consumer_key": "<ConsumerKey>",
    "consumer_secret": "<ConsumerSecret>",
    "token": "<TokenValue>",
    "token_secret": "<TokenSecret>"
  });

bricklink.getCatalogItem(ItemType.Part, '3001')
  .then(function(part){
    console.log(part);
  });

\\ Alternate Usage:

var CatalogItem = api.CatalogItem;
var req = CatalogItem.get(ItemType.Part, '3001');
bricklink.send(req)
  .then(function(part){
    console.log(part);
  });
```

## ES6 Support

Read basic usage.

```javascript
import {Client, ItemType, CatalogItem} from 'bricklink-api';

const bricklink = new Client({
    "consumer_key": "<ConsumerKey>",
    "consumer_secret": "<ConsumerSecret>",
    "token": "<TokenValue>",
    "token_secret": "<TokenSecret>"
  });

bricklink.getCatalogItem(ItemType.Part, '3001')
  .then(part => console.log(part));

\\ Alternate Usage:

let req = CatalogItem.get(ItemType.Part, '3001');
bricklink.send(req)
  .then(part => console.log(part));
```

